func:
if(msg.payload != "done")
{
    context.set("message", msg)
    context.set("isHttp", true)
    return [null, msg.payload]
}
else if(context.get("isHttp"))
{
    var newMsg = context.get("message")
    newMsg.payload = "done"
    return[newMsg, null]
}

