func:
var skill = msg.payload
var start_pos = parseInt(skill.split('#TOKEN#')[0].replace('C',''))
var end_pos_str = skill.split('#TOKEN#')[1]
var end_pos = parseInt(end_pos_str.replace('C',''))

var newMsg = {destiny: end_pos_str,
              C1: 0,
              C2: 0,
              C3: 0,
              C4: 0}

if(start_pos < end_pos){
    for(let i = start_pos; i <= end_pos; i++)
    {
        newMsg['C'.concat(i.toString())] = 1
    }

}else{
    for(let i = end_pos; i <= start_pos; i++)
    {
        newMsg['C'.concat(i.toString())] = -1
    }

}

return newMsg
